<script>
import events from '../../static/content/events'
export default {
    data() {
        return {
            events: events,
            eventsArr: [],
            eventSelected: '',
            eventType: [
                {
                    name: 'show all',
                    bg: 'bg-red-200',
                    text: 'text-red-700',
                },
                {
                    name: 'competition',
                    bg: 'bg-pink-200',
                    text: 'text-pink-700',
                },
                {
                    name: 'speaker',
                    bg: 'bg-purple-200',
                    text: 'text-purple-700',
                },
                {
                    name: 'jury',
                    bg: 'bg-yellow-200',
                    text: 'text-yellow-700',
                },
                {
                    name: 'attendee',
                    bg: 'bg-green-200',
                    text: 'text-green-700',
                },
            ],
        }
    },
    methods: {
         getImageUrl(name) {
            if (name) {
                return new URL(`@/assets/images/events/${name}`, import.meta.url).href
            }
        },
         findImage(name) {
            if (name) {
                return new URL(`~/assets/images/icon/${name}`, import.meta.url).href
            }
        },
    },
    computed: {
        retrieveEvents() {
            const event = this.events.filter(
                (event) => event.type == this.eventSelected
            )
            return event[0] ? event : this.events
        },
    },
    filters: {
        day(date) {
            var options = {
                day: 'numeric',
                timeZone: 'Indian/Mauritius',
            }
            return new Date(date).toLocaleDateString('en', options)
        },
        month(date) {
            var options = {
                month: 'short',
                timeZone: 'Indian/Mauritius',
            }
            return new Date(date).toLocaleDateString('en', options)
        },
        year(date) {
            var options = {
                year: 'numeric',
                timeZone: 'Indian/Mauritius',
            }
            return new Date(date).toLocaleDateString('en', options)
        },
    },
}
</script>

